import React from 'react';

const Table = ({ opportunities }) => {
    return (
        <div className="overflow-x-auto">
            <table className="min-w-full bg-[#232b3e] rounded-xl text-white">
                <thead>
                    <tr className="border-b border-[#2d3748]">
                        <th className="py-3 px-4 font-semibold text-lg text-left">Asset</th>
                        <th className="py-3 px-4 font-semibold text-lg text-left">Buy From</th>
                        <th className="py-3 px-4 font-semibold text-lg text-left">Sell To</th>
                        <th className="py-3 px-4 font-semibold text-lg text-left">Profit %</th>
                        <th className="py-3 px-4 font-semibold text-lg text-left">Country</th>
                    </tr>
                </thead>
                <tbody>
                    {opportunities.map((opportunity, index) => (
                        <tr key={index} className="border-b border-[#2d3748] hover:bg-[#1a2233] transition-colors">
                            <td className="py-2 px-4 flex items-center gap-2">
                                {/* Optionally add emoji/icon here: <span>ðŸ‡ºðŸ‡¸</span> */}
                                {opportunity.asset}
                            </td>
                            <td className="py-2 px-4">{opportunity.exchangeA || opportunity.buyFrom}</td>
                            <td className="py-2 px-4">{opportunity.exchangeB || opportunity.sellTo}</td>
                            <td className="py-2 px-4 text-green-400 font-semibold">{opportunity.spread || opportunity.profitMargin}%</td>
                            <td className="py-2 px-4">{opportunity.country}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Table;
