import React from 'react';

const Filters = ({ onFilterChange }) => {
    const [assetType, setAssetType] = React.useState('');
    const [region, setRegion] = React.useState('');
    const [riskLevel, setRiskLevel] = React.useState('');
    const [profitMargin, setProfitMargin] = React.useState('');

    const handleFilterChange = () => {
        onFilterChange({
            assetType,
            region,
            riskLevel,
            profitMargin,
        });
    };

    return (
        <div className="filters">
            <h2>Filter Opportunities</h2>
            <div className="filter-group">
                <label>Asset Type:</label>
                <select value={assetType} onChange={(e) => setAssetType(e.target.value)} onBlur={handleFilterChange}>
                    <option value="">All</option>
                    <option value="crypto">Crypto</option>
                    <option value="stocks">Stocks</option>
                    <option value="commodities">Commodities</option>
                    <option value="forex">Forex</option>
                </select>
            </div>
            <div className="filter-group">
                <label>Region:</label>
                <input type="text" value={region} onChange={(e) => setRegion(e.target.value)} onBlur={handleFilterChange} />
            </div>
            <div className="filter-group">
                <label>Risk Level:</label>
                <select value={riskLevel} onChange={(e) => setRiskLevel(e.target.value)} onBlur={handleFilterChange}>
                    <option value="">All</option>
                    <option value="low">Low</option>
                    <option value="medium">Medium</option>
                    <option value="high">High</option>
                </select>
            </div>
            <div className="filter-group">
                <label>Profit Margin (%):</label>
                <input type="number" value={profitMargin} onChange={(e) => setProfitMargin(e.target.value)} onBlur={handleFilterChange} />
            </div>
        </div>
    );
};

export default Filters;
