import React, { useState } from 'react';

const Table = ({ opportunities, selectedAsset, onAssetSelect, riskLevel }) => {
    // Group opportunities by asset
    const grouped = opportunities.reduce((acc, opp) => {
        if (!acc[opp.asset]) acc[opp.asset] = [];
        acc[opp.asset].push(opp);
        return acc;
    }, {});

    // If no asset selected, show asset buttons
    if (!selectedAsset) {
        return (
            <div className="flex flex-wrap gap-4 justify-center">
                {Object.keys(grouped).map(asset => (
                    <button
                        key={asset}
                        className="bg-blue-200 text-black px-4 py-2 rounded shadow hover:bg-blue-300 font-bold"
                        onClick={() => onAssetSelect(asset)}
                    >
                        {asset}
                    </button>
                ))}
            </div>
        );
    }

    // Filter by risk level if provided
    const filtered = grouped[selectedAsset].filter(opp => !riskLevel || opp.riskLevel === riskLevel);

    return (
        <div className="overflow-x-auto">
            <button className="mb-4 bg-gray-200 text-black px-3 py-1 rounded" onClick={() => onAssetSelect(null)}>
                ‚Üê Back to Assets
            </button>
            <table className="min-w-full bg-[#232b3e] rounded-xl text-black">
                <thead>
                    <tr className="border-b border-[#2d3748]">
                        <th className="py-3 px-4 font-semibold text-lg text-left">Buy From</th>
                        <th className="py-3 px-4 font-semibold text-lg text-left">Sell To</th>
                        <th className="py-3 px-4 font-semibold text-lg text-left">Profit %</th>
                        <th className="py-3 px-4 font-semibold text-lg text-left">Country</th>
                    </tr>
                </thead>
                <tbody>
                    {filtered.map((opportunity, index) => (
                        <tr key={index} className="border-b border-[#2d3748] hover:bg-[#1a2233] transition-colors">
                            <td className="py-2 px-4 text-black">{opportunity.buyFrom}</td>
                            <td className="py-2 px-4 text-black">{opportunity.sellTo}</td>
                            <td className="py-2 px-4 text-green-400 font-semibold">{opportunity.profitMargin}%</td>
                            <td className="py-2 px-4 text-black">{opportunity.country}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Table;
