import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Table from './Table';
import Filters from './Filters';
import Alerts from './Alerts';

const Dashboard = () => {
    const [opportunities, setOpportunities] = useState([]);
    const [loading, setLoading] = useState(true);
    const [filters, setFilters] = useState({
        assetType: '',
        region: '',
        riskLevel: '',
        profitMargin: ''
    });

    useEffect(() => {
        const fetchOpportunities = async () => {
            try {
                const response = await axios.get('/api/opportunities');
                setOpportunities(response.data);
            } catch (error) {
                console.error('Error fetching opportunities:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchOpportunities();
        const interval = setInterval(fetchOpportunities, 5000); // Update every 5 seconds

        return () => clearInterval(interval);
    }, []);

    const handleFilterChange = (newFilters) => {
        setFilters(newFilters);
    };

    const filteredOpportunities = opportunities.filter(opportunity => {
        // Apply filtering logic based on the filters state
        return (
            (!filters.assetType || opportunity.asset === filters.assetType) &&
            (!filters.region || opportunity.region === filters.region) &&
            (!filters.riskLevel || opportunity.riskLevel === filters.riskLevel) &&
            (!filters.profitMargin || opportunity.profitMargin >= filters.profitMargin)
        );
    });

    return (
        <div className="dashboard">
            <h1 className="text-2xl font-bold">Arbitrage Opportunities</h1>
            <Filters onFilterChange={handleFilterChange} />
            {loading ? (
                <p>Loading opportunities...</p>
            ) : (
                <Table opportunities={filteredOpportunities} />
            )}
            <Alerts opportunities={filteredOpportunities} />
        </div>
    );
};

export default Dashboard;
