import React from 'react';

const Table = ({ opportunities }) => {
    return (
        <div className="overflow-x-auto">
            <table className="min-w-full bg-white">
                <thead>
                    <tr>
                        <th className="py-2 px-4 border-b">Asset</th>
                        <th className="py-2 px-4 border-b">Exchange A</th>
                        <th className="py-2 px-4 border-b">Exchange B</th>
                        <th className="py-2 px-4 border-b">Spread %</th>
                        <th className="py-2 px-4 border-b">Estimated Profit</th>
                        <th className="py-2 px-4 border-b">Last Updated</th>
                    </tr>
                </thead>
                <tbody>
                    {opportunities.map((opportunity, index) => (
                        <tr key={index}>
                            <td className="py-2 px-4 border-b">{opportunity.asset}</td>
                            <td className="py-2 px-4 border-b">{opportunity.exchangeA}</td>
                            <td className="py-2 px-4 border-b">{opportunity.exchangeB}</td>
                            <td className="py-2 px-4 border-b">{opportunity.spread}%</td>
                            <td className="py-2 px-4 border-b">${opportunity.estimatedProfit}</td>
                            <td className="py-2 px-4 border-b">{new Date(opportunity.lastUpdated).toLocaleString()}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Table;
